      <h2>오늘의 일기</h2>
      <div>
        <input
          name="author"
          value={author}
          onChange={(e) => {
            console.log(e.target.value);
            console.log(e.target.name);
            setAutor(e.target.value);
          }}
        />
      </div>
      <div>
        <textarea></textarea>
      </div>
    </div>
  );
};
export default DiaryEditor;
